class Solution {public:    string intToRoman(int num) {        string answer;        char lookUp[8]{ "IVXLCDM" };        for (int i = 3, digits = 10000; i >= 0; --i) {            digits /= 10;            int val = num / digits;            if (!val) continue;            num -= val * digits;            int idx = i << 1;            if (val == 9) {                answer.push_back(lookUp[idx]);                answer.push_back(lookUp[idx + 2]);                continue;            }            if (val == 4) {                answer.push_back(lookUp[idx]);                answer.push_back(lookUp[idx + 1]);                continue;            }            if (val >= 5) {                answer.push_back(lookUp[idx + 1]);                val -= 5;            }            answer.append(val, lookUp[idx]);        }        return answer;    }};